import React, { useState } from 'react'
import Button from '@mui/material/Button';
import Form from 'react-bootstrap/Form';

function Login() {
  let initialData ={
    name : "",
    email : "",
    password : "",
    number : ""
}
  const [formData, setformData] = useState(initialData)
  useSt

  let handleInputChange = (e)=>{
const {name, value} = e.target;

setformData({...formData, [name] : value})
console.log(value)
  }

  let validateFunction =(e)=>{
e.prevenDefault()
if(e.target.name==="name"){
if(e.target.value===null || e.target.value===""){

}
}
  }
  
  return (
    <div className='container'>
         <Form onSubmit={validateFunction} className='form'>
      <Form.Group className="mb-3" controlId="formBasicName">
        <Form.Label>Name</Form.Label><br/>
        <Form.Control name='name' className='input-field' type="text" value={formData.name} onChange={handleInputChange} placeholder="Enter Name" />
      </Form.Group><br/>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label><br/>
        <Form.Control name='email' className='input-field' type="email" value={formData.email} onChange={handleInputChange} placeholder="Enter email" />
      </Form.Group><br/>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label><br/>
        <Form.Control name='password' pattern='[A-Z]{1}[a-z0-9]{8,20}' className='input-field' type="password" onChange={handleInputChange} placeholder="Password" />
      </Form.Group><br/>

      <Form.Group className="mb-3" controlId="formBasicNumber">
        <Form.Label>Number</Form.Label><br/>
        <Form.Control name='number' size='10' className='input-field' type='tel' value={formData.number} onChange={handleInputChange} placeholder="Mobile Number" />
      </Form.Group><br/>

      <Button variant="contained">Submit</Button>
    </Form>
    </div>
  )
}

export default Login
