import { Box, Button, Fab, Modal } from '@mui/material'
import React, { useState } from 'react'
import EditIcon from '@mui/icons-material/Edit';
function EditProduct() {

  const [openEdit, setOpenEdit] = useState(false);

  const style = {
    position: 'absolute' ,
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    pt: 2,
    px: 4,
    pb: 3,
  };
      
  return (
    <div>
         <Modal
        open={openEdit}
        
        aria-labelledby="child-modal-title"
        aria-describedby="child-modal-description"
      >
        <Box sx={{ ...style, width: 200 }}>
        <Stack
            component="form"
            sx={{
              width: "45ch",
            }}
            spacing={2}
            noValidate
            autoComplete="off"
          >
            <TextField
              hiddenLabel
              id="filled-hidden-label-normal"
              placeholder="Enter Product Name"
              variant="outlined"
              size="small"
              onChange={(e) => {
                setForm({ ...form, pName: e.target.value });
              }}
            />
            <TextField
              hiddenLabel
              id="filled-hidden-label-normal"
              placeholder="Enter Product Price"
              variant="outlined"
              size="small"
              // onChange={(e) => {
              //   setForm({ ...form, pPrice: e.target.value });
              // }}
            />
            <TextField
              hiddenLabel
              id="filled-hidden-label-normal"
              placeholder="Enter Product Description"
              variant="outlined"
              size="small"
              onChange={(e) => {
                setForm({ ...form, pDesc: e.target.value });
              }}
            />
            <div style={{ display: "flex", justifyContent: "space-around" }}>
              <Button
                disabled={error}
                onClick={() => {
                  addProductFunction();
                }}
                variant="outlined"
              >
                Submit
              </Button>
              <Button
                onClick={() => {
                  setOpen(false);
                }}
                
                variant="outlined"
              >
                Cancel
              </Button>
            </div>
            {error && <p style={{ color: "red" }}>data required</p>}
          </Stack>
        </Box>
      </Modal>
        <div><Box ><Fab onClick={()=>{setOpenEdit(true)}} >
    <EditIcon />
  </Fab></Box></div></div>
  )
}

export default EditProduct
